import React, { useState } from 'react';

const ModalChild = ({ onCloseSelf, onCloseParent }) => {
  const handleSelfClose = () => {
    // Cierra el modal hijo
    onCloseSelf();
  };

  const handleParentClose = () => {
    // Cierra el modal padre utilizando la función proporcionada
    onCloseParent();
  };

  return (
    <div>
      <h2>Contenido del Modal Hijo</h2>
      <button onClick={handleSelfClose}>Cerrar Modal Hijo</button>
      <button onClick={handleParentClose}>Cerrar Modal Padre</button>
    </div>
  );
};

const ModalParent = () => {
  const [isModalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <div>
      <button onClick={openModal}>Abrir Modal Padre</button>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            {/* Pasa funciones para cerrar ambos modales al modal hijo */}
            <ModalChild onCloseSelf={closeModal} onCloseParent={closeModal} />
          </div>
        </div>
      )}
    </div>
  );
};

const App = () => {
  return (
    <div>
      <ModalParent />
    </div>
  );
};

export default App;
