import React, { useState, useEffect } from 'react';
import './assets/HomeVistaEvento.css'
import Afiche from'./imgs/competencia.jpg'
import botonIzquierda from'./imgs/flechaIzquierda.png'
import botonDerecha from'./imgs/correcta.png'



const HomeVistaEvento = () => {
  const [imagenUrl, setImagenUrl] = useState('4');

  useEffect(() => {
    // Realiza la llamada al backend para obtener la URL de la imagen
    const obtenerImagen = async () => {
      try {
        const response = await axios.get('URL_DEL_BACKEND/devolverimagen/ID_DEL_EVENTO');
        // Asigna la URL de la imagen al estado para que se muestre en el componente
        setImagenUrl(response.data.evento);
      } catch (error) {
        console.error('Error al obtener la imagen:', error);
      }
    };

    obtenerImagen();
  }, [])



  return (
    <div className="body">
        <div className="eventos">
            <div className="evento_izquierda">
          <div className="div_img">
            <img className="imagen-izquierda" src={imagenUrl} alt="Imagen Izquierda" />
            <img src={botonIzquierda} alt="flecha-izquierda" className="boton-izquierda" />
          </div>
        </div>

          <div className="evento_actual">
            <h3 className="titulo_evento">Título evento</h3>
            <div className='img-central'>
              <img src={Afiche} alt="Imagen del Evento" className="imagen_evento" />
            </div>
            
          </div>

          <div className="evento_derecha">
            <div className="div_img">
            <img src={botonDerecha} alt="flecha-derecha" className="boton-derecha" />
              <img className="imagen-derecha" src={Afiche} alt="Imagen Derecha" />
            </div>
          </div>
        </div>

        <a href="http://localhost:3000/VerInformacionEvento" className="masinfo">
          <button className="boton_mas_informacion" type="button">
            Más información
          </button>
        </a>

        <div className="sobre_icpc">
          <hr />
          <h3>Competición internacional universitaria de programación ICPC</h3>
          <div className="descripcion_sobre_icpc">
            <p>
              La ICPC se inicia en 1970, como un programa de innovación para incrementar la aptitud de resolución de problemas, y la oportunidad para estudiantes fuertes en el campo de la computación. Con el tiempo, el concurso se convirtió en una competencia de varios niveles con el primer campeonato realizado en 1977; desde entonces el concurso se ha expandido a una colaboración mundial de universidades que albergan competencias regionales que hacen avanzar a los equipos a la ronda final anual del campeonato mundial, el ICPC World Finals.
            </p>
          </div>
        </div>

    </div>
  );
};

export default HomeVistaEvento;
