import React, { useState, useEffect } from 'react';
import axios from '../components/api/conexionApi.js';

const EventosList = () => {
  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    // Obtener la lista de eventos al cargar el componente
    axios.get('./index')
      .then(response => setEventos(response.data.eventos))
      .catch(error => console.error('Error al obtener eventos:', error));
  }, []);

  return (
    <div>
      <h1>Lista de Eventos</h1>
      <ul>
        {eventos.map(evento => (
          
          <div key={evento.idEvento} className="evento-rectangulo">
              
            <img src={evento.aficheEvento} alt={`Evento ${evento.idEvento}`} />
            
            <div className="evento-info">
              <h4>{evento.tituloEvento}</h4>
              <p>Fecha de inicio: {activity.fechaInicio}</p>
              <p>Fecha de fin: {activity.fechaFin}</p>
              {activity.descripcionActividad && <p>Descripci칩n: {activity.descripcionActividad}</p>}
              <p>Estado: {evento.estadoEvento === 1 ? 'Activo' : 'Inactivo'}</p>
              <button className="masinfo" onClick={() => handleRegistrarseClick(evento.idEvento)}>
                M치s informaci칩n
              </button>
            </div>
          </div>
        ))}
      </ul>
    </div>
  );
};

const Evento = ({ evento }) => {
  const [actividades, setActividades] = useState([]);

  useEffect(() => {
    // Obtener las actividades para el evento actual
    axios.get(`/obtenerActividadesDeEvento/${evento.id}`)
      .then(response => setActividades(response.data.actividades))
      .catch(error => console.error(`Error al obtener actividades para el evento ${evento.id}:`, error));
  }, [evento.id]);

  return (
    <li>
      <h2>{evento.nombre}</h2>
      <p>Descripci칩n: {evento.descripcion}</p>

      <h3>Actividades:</h3>
      <ul>
        {actividades.map(actividad => (
          <li key={actividad.id}>{actividad.nombreActividad}</li>
        ))}
      </ul>
    </li>
  );
};

export default EventosList;
