import React, { useState } from 'react';
import axios from './api/conexionApi';
import '../components/assets/FormCrearEvento.css';

const EventForm = () => {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [modalVisible, setModalVisible] = useState(false);
  const [nombreError, setNombreError] = useState('');
  const [descripcionError, setDescripcionError] = useState('');
  
  const handleNombreChange = (e) => {
    setNombre(e.target.value);
    setNombreError('');
  };
 
  const handleDescripcionChange = (e) => {
    setDescripcion(e.target.value);
    setDescripcionError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!nombre) {
      setNombreError('El nombre es obligatorio.');
      return;
    }

    if (nombre.length > 25) {
       setNombreError('El campo de nombre no debe exceder los 25 caracteres.');
      return;
    }
    

    if (!/^[A-Za-z\s]+$/.test(nombre)) {
      setNombreError('El nombre no debe contener números ni caracteres especiales.');
      return;
    }

    const formData = new FormData();
    //formData.append('titulo', titulo);
    formData.append('nombreTipoEvento', nombre);
    formData.append('descripcionTipoEvento', descripcion);

    try {
      // Realizar la solicitud POST con formData usando axios
      await axios.post('crearTipoEvento', formData);
      setModalVisible(true);
      console.log('Evento creado con éxito');
    } catch (error) {
      console.error('Error al crear evento', error);
      setModalVisible(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="tweet-composer">

      <div className="composer-form">
        <label htmlFor="nombre">Nombre  <span style={{ color: 'red' }}>*</span></label>
        <input
          type="text"
          id="nombre"
          name="nombre"
          placeholder="Nombre del tipo de evento"
          value={nombre}
          onChange={handleNombreChange}
          required
          
        />
      </div>
        
<p className="error-message">{nombreError}</p>
        
<br></br>

     <div className="composer-form">
        <label htmlFor="Descripcion">Descripción  </label>
        <textarea
          id="descripcion"
          name="descripcion"
          placeholder="Descripción del evento"
          rows="4"
          value={descripcion}
          onChange={handleDescripcionChange}
          
        ></textarea>

    </div>
        <p className="error-message">{descripcionError}</p> {/* Nuevo */}


        

      
{/* 

<br>
</br> */}
{modalVisible && (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={() => setModalVisible(false)}>
            
          </span>
          <p>Creado con exito!</p>
          
        </div>
      </div>
    )}

       <div className='tipoeventob'>
         <button className="tweet-button" type="submit" onClick={() =>window.location.href = 'http://localhost:3000/home'}>
            Cancelar
          </button>
        
          <button className="tweet-button" type="submit">
            Crear
          </button>
        </div> 

     {/*  </div> */}
    </form>
  );
};

export default EventForm;
