import React, { useState, useEffect } from 'react';
import './assets/HomeVistaEvento.css'
import axios from '../components/api/conexionApi';
import Afiche from'./imgs/competencia.jpg'
import botonIzquierda from'./imgs/flechaIzquierda.png'
import botonDerecha from'./imgs/correcta.png'
import Slider from 'react-slick';

const HomeVistaEvento = () => {
  const [eventos, setEventos] = useState([]);
  const handleRegistrarseClick = async() => {
    setEventos(  axios.get('./index'));
   
    const ultimoId = response.data.ultimoId;
    window.location.href = `./VerInformacionEvento?id=${ultimoId}`;
}




const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 3,
  slidesToScroll: 1,
};


  
  return (
    <div className="body">
      <h3 className="titulo_home">Título evento</h3>  
      <hr />

      <Slider {...settings}>
    {eventos.map(evento => (
      <div key={evento.id}>
        <img src={evento.imagen} alt={`Evento ${evento.id}`} />
      </div>
    ))}
  </Slider>
        
        
        
        
            <div className='boton_mas_informacion'>
          <button className="masinfo" type="submit" onClick={handleRegistrarseClick} >
              Más información
          </button>
        </div>


       
    </div>
  );
};

export default HomeVistaEvento;
