import React, { useState, useEffect } from 'react';
import axios from './api/conexionApi';
import './assets/VerInformacionEvento.css'

const EventoId=1;
const nombreActividadBuscada = "Cronograma general";

const handleRegistrarseClick = () => {
    
    window.location.href = './registroParticipantes';
}
const EventoComponent = () => {
    //tabla evento
    const [evento, setEvento] = useState(null);
    useEffect(() => {
        axios.get(`eventos/${EventoId}`) 
            .then(response => {
                setEvento(response.data.evento);
            })
            .catch(error => {
                console.error('Error al obtener el evento:', error);
            });
    }, []);

    //tabla regla
            const [reglas, setReglas] = useState([]);
            useEffect(() => {
            axios.get(`obtenerReglasDeEvento/${EventoId}`)
            .then(response => {
                setReglas(response.data.reglas);
            })
            .catch(error => {
                console.error('Error al obtener las reglas:', error);
            });
            }, []);

        //tabla requisito
        const [requisitos, setRequisitos] = useState([]);
            useEffect(() => {
            axios.get(`obtenerRequisitosDeEvento/${EventoId}`)
            .then(response => {
                setRequisitos(response.data.requisitos);
            })
            .catch(error => {
                console.error('Error al obtener los requisitos:', error);
            });
        }, []);

            //tabla actividad
            const [actividades, setActividades] = useState([]);
            useEffect(() => {
              axios.get(`obtenerActividadesDeEvento/${EventoId}`)
                .then(response => {
                  setActividades(response.data.actividades);
                })
                .catch(error => {
                  console.error('Error al obtener las actividades:', error);
                });
            }, []);

            const [actividadEncontrada, setActividadEncontrada] = useState([]);
            useEffect(() => {
              axios.get(`buscarActividadPorNombre/${EventoId}/${encodeURIComponent(nombreActividadBuscada)}`)
                   .then(response => {
                    setActividadEncontrada(response.data.actividadPorNombre);
                    console.log(encodeURIComponent(nombreActividadBuscada))
                })
                .catch(error => {
                  console.error('Error al obtener la actividad cronograma', error);
                });
            }, []);
            
            if (actividades) {
                console.log("Actividad encontrada:", actividades);
              } else {
                console.log("No se encontrÃ³ ninguna actividad con el nombre 'Cronograma general'");
              }   
              
              
              

    if (!evento) {
        return <div>Cargando evento...</div>;
    }
    
    if (!requisitos) {
        return <div>Cargando requisitos ...</div>;
    }

    if (!reglas) {
        return <div>Cargando reglas ...</div>;
    }

    if (!actividades) {
        return <div>Cargando actividades ...</div>;
    }
    
    if (!actividadEncontrada) {
        return <div>Cargando datos de cronograma del evento ...</div>;
    }else{
        console.log(" la actividad encontrada es  es")
        console.log(actividadEncontrada)
    }
    

    
    

    const descripcionConSaltos = evento.descripcionEvento.split('\n').map((item, i) => (
        <p key={i}>{item}</p>
    ));
    return (
        <div className="all-info-container">
            <div className='info-box'>
                <h1>{evento.tituloEvento}</h1>
            </div>
           
            <br />

           
            <div className='info-box'>
                <strong>Fecha Inicio </strong>
                <p>{actividadEncontrada.nombreActividad}</p>
            </div>

            <br />


            
           

            <div className='date-container'>
            
                    <div className='date-box'>
                    <strong>Estado:</strong>
                    <p className={`info-box ${evento.estadoEvento ? 'estado-activo' : 'estado-inactivo'}`}><strong>{evento.estadoEvento ? 'Activo' : 'Inactivo'}</strong></p>
                    </div>
                 
                    
            </div>
        
            <br />
            

            
            <div className='date-box'>
                <strong>Dinamica:</strong>  
                <p className='info-box'> {evento.participacion}</p>
        <br />

                {evento.participacion === 'Equipo' && (

                <div className='date-container'>
                    <>  

                    <div className='date-box'>
                        <strong>Cantidad de entrenadores:</strong>
                        <p className='info-box'> {evento.numEntrenadores}</p>
                    </div>
                    
                    <div className='date-box'>
                        <strong>Cantidad de participantes:</strong>
                        <p className='info-box'> {evento.numParticipantes}</p>
                    </div>
                </>
                </div>
            )}

             {evento.participacion === 'Individual' && (
        
            <div >
            
            </div>
                )}
            </div>


        <br />
            <div className='date-box'>
                <strong>Modalidad:</strong>
                <p className='info-box'> {evento.modalidad}</p>
            </div>
            
        <br />
        
            <div className='info-box'>
                <strong>Descripcion:</strong>
                {descripcionConSaltos}
