import React, { useState } from 'react';
import axios from './api/conexionApi';
import './assets/registroParticipantes.css';
import Validaciones from "./utils/Validaciones";
const evento=1;
const FormularioRegistroParticipantes = (idEvento) => {
  const [formData, setFormData] = useState({
    idPersona: '',
    nombrePersona: '',
    apellidoPersona: '',
    genero: '',
    telefonoPersona: ''
  });
  const [correoData, setCorreoData] = useState({
    correoC:'',
    //estadoNotificacion:'',
    //idPersona: ''
    
  });
 //para el error
 const [nombreError, setNombreError] = useState("");
  const [apellidoError, setApellidoError] = useState("");
 
  const [correoError, setCorreoError] = useState("");
  const [idPersonaError, setIdPersonaError] = useState("");

  const handleChangeCorreo = (e) => {
    const { name, value } = e.target;
    setCorreoData({
      ...correoData,
      [name]: value
    });

  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    /*setFormData({
      ...formData,
      [name]: value
    });*/
    switch (name) {
      case 'nombrePersona':
          case 'apellidoPersona':
            // Permitir solo caracteres alfabéticos y espacios, con un límite de 30 caracteres
            const newValueAlpha = value.replace(/[^a-zA-Z\s]/g, '').substring(0, 30);
            
            if (value.length > 30) {
            //  setErrorMessage(`El campo ${name} no puede superar los 30 caracteres`);
            setErrorMessages({
              ...errorMessages,
              [name]: errorTreintaCaracteres,
            });
            } else if (newValueAlpha !== value) {
              setErrorMessages({
                  ...errorMessages,
                  [name]: errorCaracteresNoPermitidos,
                });
             // setErrorMessage(`Caracteres no permitidos en ${name}`);
            } else {
              //setErrorMessage('');
              setErrorMessages({
                  ...errorMessages,
                  [name]: '',
                });
            }
      
            setFormData({
              ...formData,
              [name]: newValueAlpha,
            });
            break;

      case 'idPersona':
        const newValueID = value.replace(/[^\d]/g, '').substring(0, 8);
        
    if (newValueID !== value) {
      setErrorMessages({
        ...errorMessages,
        idPersona: errorSoloNumeros,
      });
    } else if (value.length > 8) {
      setErrorMessages({
          ...errorMessages,
          idPersona: errorOchoCaracteres,
        });
      
    } else if (value.length < 7) {
      setErrorMessages({
          ...errorMessages,
          idPersona: errorSieteCaracteres,
        });
    } else {
      setErrorMessages({
        ...errorMessages,
        idPersona: '',
      });
    }

    setFormData({
      ...formData,
      [name]: newValueID,
    });
    break;
              

      case 'genero':
    if (value === 'M' || value === 'F'  ) {
      console.log("genroooo",value)
      setErrorMessages({
        ...errorMessages,
        genero: '',
      });
      setFormData({
        ...formData,
        [name]: value,
      });
    } else {
      setErrorMessages({
        ...errorMessages,
        genero: errorSeleccioneUnaOpcion,
      });
    }

    
    break;

      case 'telefonoPersona':
        const newValueTel = value.replace(/[^\d]/g, '').substring(0, 8);
        if(value.length<7 ){
          if(newValueTel!==value){
              setErrorMessages({
                  ...errorMessages,
                  telefonoPersona:errorSoloNumeros,
              });
          }else{
          setErrorMessages({
              ...errorMessages,
              telefonoPersona:errorSieteCaracteres,
          });}
          }else{
              if(value.length>8){
                  setErrorMessages({
                   ...errorMessages,
                   telefonoPersona:errorOchoCaracteres,
                  });
              }else{                    
                  setErrorMessages({
                  ...errorMessages,
                  telefonoPersona:'',
                  });
              }
          }

        setFormData({
          ...formData,
          [name]: newValueTel,
        });
        break;

      default:
        setFormData({
          ...formData,
          [name]: value,
        });
    }
};

  
  const handleSubmit = (e) => {
    //ERRORES
    if (formData.nombrePersona.length < 3 || formData.nombrePersona.length > 30) {
      setNombreError("Por favor, ingrese un nombre válido (entre 3 y 30 caracteres).");
      return;
    } else {
      setNombreError("");
    }

    if (formData.apellidoPersona.length < 3 || formData.apellidoPersona.length > 30) {
      setApellidoError("Por favor, ingrese un apellido válido (entre 3 y 30 caracteres).");
      return;
    } else {
      setApellidoError("");
    }
    
    if (formData.idPersona.length < 7 || formData.idPersona.length > 8) {
      setIdPersonaError("Por favor, ingrese CI válido (entre 7 y 8 caracteres).");
      return;
    } else {
      setIdPersonaError("");
    }
    if (correoData.correoC.length < 1 ) {
      setCorreoError("Por favor, ingrese un correo electronico");
      return;
    } else {
      setCorreoError("");
    }
//ERRORES
    e.preventDefault();
    axios.post('./storePersona', formData)
      .then((a) => {
        
       console.log('Datos guardados correctamente', formData.idPersona);
       const formCorreo = {
        correoC: correoData.correoC,   
        estadoNotificacion: true,
        idEvento: formData.idPersona
      };
       axios.post('./correos', formCorreo)
          .then((c)=>{
            console.log('correo guardado', correoData.correoC);
            const formParticipante = {
              idParticipante: formData.idPersona,   
              idEvento: evento,
              idEquipo: null
            };

              console.log('Datos editados', formParticipante.idParticipante);

              axios.post('./storeParticipante', formParticipante)
              .then((b)=>{

                    console.log('Datos de participante guardados correctamente', formParticipante.idParticipante);

              })
              .catch((error) => {
          
                console.error('Error al guardar participante ', error);
              });
          setFormData({
            idPersona: '',
            nombrePersona: '',
            apellidoPersona: '',
            genero: '',
            telefonoPersona: ''
          });
          setCorreoData({
            correoC:''
          });
          
      })
      .catch((error) => {
       
        console.error('Error al guardar los datos', error);
      });
      alert("Datos guardados exitosamente.");
  });
  }



  return (
    <div>
    <form onSubmit={handleSubmit}>
            
      <div>
        <label htmlFor="nombrePersona">Nombre:</label>
        <input
          type="text"
          id="nombrePersona"
          name="nombrePersona"
          value={formData.nombrePersona}
          placeholder="Ingresa tu nombre"
          onChange={handleChange}
        />
         <p style={{ color: 'red' }}>{nombreError}</p>
      </div>
      <div>
        <label htmlFor="apellidoPersona">Apellido:</label>
        <input
          type="text"
          id="apellidoPersona"
          name="apellidoPersona"
          value={formData.apellidoPersona}
          placeholder="Ingresa tus apellidos"
          onChange={handleChange}          
        />
         <p style={{ color: 'red' }}>{apellidoError}</p>
      </div>

      <div>
        <label htmlFor="idPersona">ID de Persona:</label>
        <input
          type="text"
          id="idPersona"
          name="idPersona"
          value={formData.idPersona}
          placeholder="Ingresa tu numero de identificacion"
          onChange={handleChange}          
        />
         <p style={{ color: 'red' }}>{idPersonaError}</p>
      </div>

      <div>
        <label htmlFor="genero">Género:</label>
        <select
          id="genero"
          name="genero"
          value={formData.genero}
          required
          onChange={handleChange}
        >
          <option value="F">Femenino</option>
          <option value="M">Masculino</option>
        </select>
      </div>

     
      <div>
        <label htmlFor="telefonoPersona">Teléfono:</label>
        <input
          type="text"
          id="telefonoPersona"
          name="telefonoPersona"
          value={formData.telefonoPersona}
          placeholder="60004533"
          onChange={handleChange}
        />
        
      </div>
      <div>
        <label htmlFor="correoC">Correo:</label>
        <input
          type="text"
          id="correoC"
          name="correoC"
          value={correoData.correoC}
          placeholder="example@gmail.com"
          onChange={handleChangeCorreo}
        />
         <p style={{ color: 'red' }}>{correoError}</p>
      </div>

      


 
      
      <button type="submit">Guardar</button>
    </form>
    
    
      </div>


  );


}
export default FormularioRegistroParticipantes;
