import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import   '../components/assets/MyCalendar.css'
const localizer = momentLocalizer(moment);
const MiCalendario = () => {
  const [eventos, setEventos] = useState([
    {
      title: 'Evento 1',
      start: new Date(2023, 10, 1, 10, 0),
      end: new Date(2023, 10, 1, 12, 0),
    },
    // Agrega más eventos según tus necesidades
  ]);

  const customEventStyleGetter = (event, start, end, isSelected) => {
    const style = {
      backgroundColor: '#FFC107', // Cambia el color según tus preferencias
      borderRadius: '0px',
      opacity: 0.8,
      color: 'black',
      border: '0',
      display: 'block',
      font: 'inherit',
    };

    return {
      style: style
    };
  };

  return (
    <div className="mi-contenedor">
      <Calendar
        localizer={localizer}
        events={eventos}
        startAccessor="start"
        endAccessor="end"
        eventPropGetter={customEventStyleGetter} // Aplica la personalización de estilo aquí
      />
    </div>
  );
};

export default MiCalendario;
