import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import '../components/assets/MyCalendar.css';

const localizer = momentLocalizer(moment);

const MiCalendario = ({ fechaInicio, fechaFin }) => {
  const [eventos, setEventos] = useState([
    {
        title: 'Evento 1',
        start: new Date(2023, 11, 20, 10, 0),
        end: new Date(2024, 0, 10, 12, 0),
      },
      {
        title: 'Evento 2',  // Título del segundo evento
        start: new Date(2023, 11, 25, 8, 0), // Fecha y hora de inicio
        end: new Date(2023, 11, 25, 12, 0),   // Fecha y hora de fin
      },
  ]);

 
  const customDayPropGetter = (date) => {
    const customStyle = {};

    // Define la fecha que deseas destacar
    const fechaEspecifica = new Date(fechaInicio); 
    if (moment(date).isSame(fechaEspecifica, 'day')) {
      customStyle.backgroundColor = 'pink'; // Color de fondo rosa
    }

    return {
      style: customStyle,
    };
  };

  const dayStyleGetter = (date) => {
    // Comprueba si hay eventos en la fecha actual
    const hasEvents = eventos.some(evento => {
      const eventStartDate = new Date(evento.start);
      const eventEndDate = new Date(evento.end);
      return date >= eventStartDate && date <= eventEndDate;
    });

    // Si hay eventos, aplica un fondo rosa, de lo contrario, no hace cambios
    return hasEvents ? { style: { backgroundColor: 'pink' } } : {};
  };

  return (
    <Calendar
  localizer={localizer}
  events={eventos} // Aquí se pasa la lista de eventos al calendario
  startAccessor="start"
  endAccessor="end"
  className="mi-calendario"
  views={['month', 'agenda']}
  toolbar={true}
  eventPropGetter={eventStyleGetter}
  dayPropGetter={(date) => {
    const customStyle = customDayPropGetter(date).style;
    const eventStyle = dayStyleGetter(date).style;
    return { style: { ...customStyle, ...eventStyle } };
  }}
/>
  );
};

export default MiCalendario;
