import React, { useState } from 'react';
import axios from 'axios';
import Alert from'./Alert';


const ModalRegla = ({ mostrar, cerrar, nombre, descripcion, setDescripcion, setNombre, agregarRegla }) => {
  return (
    mostrar && (
      <div className="ventana-emergente">
        <div className="modalCrearEvento">
          <div className="FondoModal1">
            <h2 className="CrearEvento">Añadir regla</h2>
          </div>

          <div className="FondoModal2">
            <div className='Campovacio'>
              <label htmlFor="nombreRegla">Nombre de regla:</label>
              <div className="ColorCampoVacio">*</div>
            </div>
            <input
              type="text"
              id="nombreRegla"
              value={nombre}
              placeholder='Nombre'
              onChange={(e) => setNombre(e.target.value)}
              onBlur={() => (setNombreError(nombre.trim() === ""), setError(nombre.trim() === ""))}
              className={nombreError ? "campo-vacio" : ""}
            />
            {(nombre.length > 40)&& <div className="ErrorForm">máximo 40 caracteres alfanuméricos</div>}

        <div className='Campovacio'> 
            <label htmlFor="descripcionReqglas">Reglas:</label>
            <div className="ColorCampoVacio">*</div>
          </div>
          <textarea
            id="descripcionReglas"
            className="descripcionModal"
            value={descripcion}
            placeholder='Descripcion'
            onChange={(e) => setDescripcion(e.target.value)}
          ></textarea>
          {(descripcion.length > 200)&& <div className="ErrorForm">máximo 200 caracteres alfanuméricos</div>}
          
            <div className="ConfbuttonRequisitos">
              <button className="buttonRequisitos" type="button" onClick={cerrar}>
                Cancelar
              </button>
              <button className="buttonRequisitos" type="button" onClick={agregarRegla}>
                Agregar
              </button>
            </div>
          </div>
        </div>
      </div>
    )
  );
};

export default ModalRegla;
