import React, { useState } from 'react';
import axios from 'axios';
import './assets/configurarEvento.css';

const ConfiguracionEvento = ({ onGuardarCampos }) => {
  const [opcionesSeleccionadas, setOpcionesSeleccionadas] = useState([]);
  const [mostrarSubopciones, setMostrarSubopciones] = useState(false);
  const [subopcionSeleccionada, setSubopcionSeleccionada] = useState('');

  const opciones = [
    'Descripción', 
    'Requisitos', 
    'Actividades', 
    'Reglas', 
    'Premios', 
    'Participación',
  ];

  const handleOpcionClick = (opcion) => {
    if (opcion === 'Participación') {
      if (!opcionesSeleccionadas.includes(opcion)) {
        // Si se selecciona la opción 'Participación', la marcamos y mostramos las subopciones.
        setOpcionesSeleccionadas([...opcionesSeleccionadas, opcion]);
        setMostrarSubopciones(true);
      } else {
        // Si ya está seleccionada, desmarcamos y ocultamos las subopciones.
        setOpcionesSeleccionadas(opcionesSeleccionadas.filter((item) => item !== opcion));
        setMostrarSubopciones(false);
        setSubopcionSeleccionada('');
      }
    } else {
      if (opcionesSeleccionadas.includes(opcion)) {
        setOpcionesSeleccionadas(opcionesSeleccionadas.filter((item) => item !== opcion));
      } else {
        setOpcionesSeleccionadas([...opcionesSeleccionadas, opcion]);
      }
    }
  };

  const handleSubopcionChange = (subopcion) => {
    // Lógica para seleccionar una de las subopciones.
    setSubopcionSeleccionada(subopcion);
  };

  const handleGuardar = () => {
    const camposSeleccionados = {
      opciones: opcionesSeleccionadas,
      subopcion: subopcionSeleccionada,
    };

    // Aquí puedes hacer una solicitud HTTP para enviar los datos al servidor, por ejemplo:
    // axios.post('/api/guardarCampos', camposSeleccionados)
    //   .then(response => {
    //     console.log(response.data);
    //   })
    //   .catch(error => {
    //     console.error(error);
    //   });

    // Llamamos a la función proporcionada por el padre para pasar los datos.
    onGuardarCampos(camposSeleccionados);
  };

  return (
    <div className="formulario-container">
      <h2>Campos requeridos para el evento</h2>
      <div className="columnas-container">
        <div className="columna">
          {opciones.slice(0, 3).map((opcion) => (
            <label key={opcion}>
              <input
                type="checkbox"
                checked={opcionesSeleccionadas.includes(opcion)}
                onChange={() => handleOpcionClick(opcion)}
              /> {opcion}
            </label>
          ))}
        </div>
        <div className="columna">
          {opciones.slice(3, 6).map((opcion) => (
            <label key={opcion}>
              <input
                type="checkbox"
                checked={opcionesSeleccionadas.includes(opcion)}
                onChange={() => handleOpcionClick(opcion)}
              /> {opcion}
            </label>
          ))}
        </div>
      </div>

      
      
      {/* Usamos un botón en lugar de un enlace para evitar la recarga de la página */}
      <div className="botones">
        <button className="boton-cancelar" type="button" onClick={() => window.history.back()}>Cancelar</button>
        <a href="./CrearEvento">
          <button className="boton-siguiente" onClick={handleGuardar}>Siguiente</button>
        </a>
      </div>
    </div>
  );
};

export default ConfiguracionEvento;
