import React, { useState } from 'react';
import axios from 'axios';
import './assets/registroParticipantes.css';

const FormularioRegistro = () => {
  const [nombre, setNombre] = useState('');
  const [apellidos, setApellidos] = useState('');
  const [correo, setCorreo] = useState('');
  const [ci, setCI] = useState('');
  const [celular, setCelular] = useState('');
  const [equipo, setEquipo] = useState('');
  const [genero, setGenero] = useState('');
  const [rol, setRol] = useState('');
  const [institucion, setInstitucion] = useState('');
  const [codigoSIS, setCodigoSIS] = useState('');
  const [semestre, setSemestre] = useState('');
  const [notificaciones, setNotificaciones] = useState(false);

  const handleInputChange = (e, stateSetter) => {
    stateSetter(e.target.value);
  };

  const handleCheckboxChange = (e) => {
    setNotificaciones(e.target.checked);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Envía los datos del formulario a través de una solicitud AJAX (puedes usar Axios u otra biblioteca)
      await axios.post('http://localhost:8000/api/registro', {
        nombre,
        apellidos,
        correo,
        ci,
        celular,
        equipo,
        genero,
        rol,
        institucion,
        codigoSIS,
        semestre,
        notificaciones: notificaciones ? 1 : 0,
      });
      console.log('Registro exitoso');
    } catch (error) {
      console.error('Error en el registro:', error);
      if (error.response) {
        // La solicitud se realizó, pero el servidor respondió con un código de estado fuera del rango 2xx
        console.error('Respuesta del servidor con error:', error.response.data);
        console.error('Código de estado HTTP:', error.response.status);
      } else if (error.request) {
        // La solicitud se hizo, pero no se recibió respuesta
        console.error('No se recibió respuesta del servidor:', error.request);
      } else {
        // Algo sucedió en la configuración de la solicitud que provocó el error
        console.error('Error durante la configuración de la solicitud:', error.message);
      }
    }
  };

  return (
    <div className="tweet-composer">
      <div className="composer-form">
        <form id="registration-form" onSubmit={handleSubmit}>
          <h1>Datos Personales</h1>

          <label htmlFor="nombre">
            Nombre: <span style={{ color: 'red' }}>*</span>
          </label>
          <input
            type="text"
            id="nombre"
            name="nombre"
            placeholder="Nombre"
            value={nombre}
            onChange={(e) => handleInputChange(e, setNombre)}
            required
          />

          <label htmlFor="apellidos">
            Apellidos: <span style={{ color: 'red' }}>*</span>
          </label>
          <input
            type="text"
            id="apellidos"
            name="apellidos"
            placeholder="Apellidos"
            value={apellidos}
            onChange={(e) => handleInputChange(e, setApellidos)}
            required
          />

          <label htmlFor="correo">
            Correo electrónico: <span style={{ color: 'red' }}>*</span>
          </label>
          <input
            type="email"
            id="correo"
            name="correo"
            placeholder="Correo electrónico"
            value={correo}
            onChange={(e) => handleInputChange(e, setCorreo)}
            required
          />

          <div className="split-row">
            <div className="split-column">
              <label htmlFor="ci">
                C.I.: <span>*</span>
              </label>
              <input
                type="text"
                id="ci"
                name="ci"
                placeholder="Carnet de identidad"
                value={ci}
                onChange={(e) => handleInputChange(e, setCI)}
                required
              />

              <label htmlFor="celular">
                Celular: <span>*</span>
              </label>
              <input
                type="text"
                id="celular"
                name="celular"
                placeholder="Celular"
                value={celular}
                onChange={(e) => handleInputChange(e, setCelular)}
                required
              />
            </div>
            <div className="split-column">
              <fieldset className="role-fieldset">
                <legend>Género</legend>
                <div className="radio-group">
                  <label htmlFor="femenino">
                    <input
                      type="radio"
                      id="femenino"
                      name="genero"
                      value="Femenino"
                      checked={genero === 'Femenino'}
                      onChange={(e) => handleInputChange(e, setGenero)}
                    />
                    Femenino
                  </label>
                  <label htmlFor="masculino">
                    <input
                      type="radio"
                      id="masculino"
                      name="genero"
                      value="Masculino"
                      checked={genero === 'Masculino'}
                      onChange={(e) => handleInputChange(e, setGenero)}
                    />
                    Masculino
                  </label>
                </div>
              </fieldset>
            </div>
          </div>

          <h1>Datos Participante</h1>
          <div className="split-row">
            <div className="split-column">
              <fieldset className="role-fieldset">
                <legend>Rol participante</legend>
                <div className="radio-group">
                  <label htmlFor="entrenador">
                    <input
                      type="radio"
                      id="entrenador"
                      name="rol"
                      value="Entrenador"
                      checked={rol === 'Entrenador'}
                      onChange={(e) => handleInputChange(e, setRol)}
                    />
                    Entrenador
                  </label>
                  <label htmlFor="participante">
                    <input
                      type="radio"
                      id="participante"
                      name="rol"
                      value="Participante"
                      checked={rol === 'Participante'}
                      onChange={(e) => handleInputChange(e, setRol)}
                    />
                    Participante
                  </label>
                </div>
              </fieldset>
            </div>

            <div className="split-column">
              <label>Equipo perteneciente:</label>
              <select
                id="equipo"
                name="equipo"
                value={equipo}
                onChange={(e) => handleInputChange(e, setEquipo)}
              >
                <option value="Equipo 1">Equipo 1</option>
                <option value="Equipo 2">Equipo 2</option>
                <option value="Equipo 3">Equipo 3</option>
              </select>
            </div>
          </div>

          <div className="split-row">
            <div className="split-column">
              <label htmlFor="institucion">Institución:</label>
              <input
                type="text"
                id="institucion"
                name="institucion"
                placeholder="Institución"
                value={institucion}
                onChange={(e) => handleInputChange(e, setInstitucion)}
              />
            </div>
            <div className="split-column">
              <label htmlFor="codigoSIS">Código SIS:</label>
              <input
                type="text"
                id="codigoSIS"
                name="codigoSIS"
                placeholder="Código SIS"
                value={codigoSIS}
                onChange={(e) => handleInputChange(e, setCodigoSIS)}
              />
            </div>
            <div className="split-column">
              <label htmlFor="semestre">Semestre:</label>
              <select
                id="semestre"
                name="semestre"
                value={semestre}
                onChange={(e) => handleInputChange(e, setSemestre)}
              >
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
                <option value="6">6</option>
                <option value="7">7</option>
                <option value="8">8</option>
                <option value="9">9</option>
                <option value="10">10</option>
              </select>
            </div>
          </div>

          <div className="split-row">
            <div className="split-column">
              <label className="multiline-label" htmlFor="notificaciones">
                <input
                  type="checkbox"
                  id="notificaciones"
                  name="notificaciones"
                  checked={notificaciones}
                  onChange={handleCheckboxChange}
                />
                Deseo recibir<br />notificaciones del evento
              </label>
            </div>
            <div className="container">
             <div className="other-elements"></div>
              \<div>
                <button className="tweet-button" type="submit" onClick={() => window.history.back()}>
                Cancelar
                </button>
                <button className="tweet-button" type="submit">
                Guardar
                </button>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FormularioRegistro;
