import React, { useState } from 'react';
import axios from './api/conexionApi';
import './assets/registroParticipantes.css';
import Validaciones from "./utils/Validaciones";
const evento=1;
const FormularioRegistroParticipantes = (idEvento) => {
  const [formData, setFormData] = useState({
    idPersona: '',
    nombrePersona: '',
    apellidoPersona: '',
    genero: '',
    telefonoPersona: ''
  });
  const [correoData, setCorreoData] = useState({
    correoC:'',
    //estadoNotificacion:'',
    //idPersona: ''
    
  });
 //para el error
 const [nombreError, setNombreError] = useState("");
  const [apellidoError, setApellidoError] = useState("");
 
  const [correoError, setCorreoError] = useState("");
  const [idPersonaError, setIdPersonaError] = useState("");

  const handleChangeCorreo = (e) => {
    const { name, value } = e.target;
    setCorreoData({
      ...correoData,
      [name]: value
    });

  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    //errores
    switch (e.target.name) {
      case "nombrePersona":
        const nombre = e.target.value;
        const nombreError = Validaciones.validarNombre(nombre);
        setNombreError(nombreError);
        break;
      
        case "apellidoPersona":
          const apellido = e.target.value;
          const errorApellido = Validaciones.validarNombre(apellido);
          setApellidoError(errorApellido);
          break;

          case "idPersona":
        const ci = e.target.value;
        const error = Validaciones.validarNombre(nombre);
        setNombreError(nombreError);
        break;
      default:
        break;
    }
    //errores
  };
 
  
  const handleSubmit = (e) => {
    //ERRORES
    if (formData.nombrePersona.length < 3 || formData.nombrePersona.length > 30) {
      setNombreError("Por favor, ingrese un nombre válido (entre 3 y 30 caracteres).");
      return;
    } else {
      setNombreError("");
    }

    if (formData.apellidoPersona.length < 3 || formData.apellidoPersona.length > 30) {
      setApellidoError("Por favor, ingrese un apellido válido (entre 3 y 30 caracteres).");
      return;
    } else {
      setApellidoError("");
    }
    
    if (formData.idPersona.length < 7 || formData.idPersona.length > 8) {
      setIdPersonaError("Por favor, ingrese CI válido (entre 7 y 8 caracteres).");
      return;
    } else {
      setIdPersonaError("");
    }
    if (correoData.correoC.length < 1 ) {
      setCorreoError("Por favor, ingrese un correo electronico");
      return;
    } else {
      setCorreoError("");
    }
//ERRORES
    e.preventDefault();
    axios.post('./storePersona', formData)
      .then((a) => {
        
       console.log('Datos guardados correctamente', formData.idPersona);
       const formCorreo = {
        correoC: correoData.correoC,   
        estadoNotificacion: true,
        idEvento: formData.idPersona
      };
       axios.post('./correos', formCorreo)
          .then((c)=>{
            console.log('correo guardado', correoData.correoC);
            const formParticipante = {
              idParticipante: formData.idPersona,   
              idEvento: evento,
              idEquipo: null
            };

              console.log('Datos editados', formParticipante.idParticipante);

              axios.post('./storeParticipante', formParticipante)
              .then((b)=>{

                    console.log('Datos de participante guardados correctamente', formParticipante.idParticipante);

              })
              .catch((error) => {
          
                console.error('Error al guardar participante ', error);
              });
          setFormData({
            idPersona: '',
            nombrePersona: '',
            apellidoPersona: '',
            genero: '',
            telefonoPersona: ''
          });
          setCorreoData({
            correoC:''
          });
          
      })
      .catch((error) => {
       
        console.error('Error al guardar los datos', error);
      });
      alert("Datos guardados exitosamente.");
  });
  }



  return (
    <div>
    <form onSubmit={handleSubmit}>
            
      <div>
        <label htmlFor="nombrePersona">Nombre:</label>
        <input
          type="text"
          id="nombrePersona"
          name="nombrePersona"
          value={formData.nombrePersona}
          placeholder="Ingresa tu nombre"
          onChange={handleChange}
        />
         <p style={{ color: 'red' }}>{nombreError}</p>
      </div>
      <div>
        <label htmlFor="apellidoPersona">Apellido:</label>
        <input
          type="text"
          id="apellidoPersona"
          name="apellidoPersona"
          value={formData.apellidoPersona}
          placeholder="Ingresa tus apellidos"
          onChange={handleChange}          
        />
         <p style={{ color: 'red' }}>{apellidoError}</p>
      </div>

      <div>
        <label htmlFor="idPersona">ID de Persona:</label>
        <input
          type="text"
          id="idPersona"
          name="idPersona"
          value={formData.idPersona}
          placeholder="Ingresa tu numero de identificacion"
          onChange={handleChange}          
        />
         <p style={{ color: 'red' }}>{idPersonaError}</p>
      </div>

      <div>
        <label htmlFor="genero">Género:</label>
        <select
          id="genero"
          name="genero"
          value={formData.genero}
          required
          onChange={handleChange}
        >
          <option value="F">Femenino</option>
          <option value="M">Masculino</option>
        </select>
      </div>

     
      <div>
        <label htmlFor="telefonoPersona">Teléfono:</label>
        <input
          type="text"
          id="telefonoPersona"
          name="telefonoPersona"
          value={formData.telefonoPersona}
          placeholder="60004533"
          onChange={handleChange}
        />
        
      </div>
      <div>
        <label htmlFor="correoC">Correo:</label>
        <input
          type="text"
          id="correoC"
          name="correoC"
          value={correoData.correoC}
          placeholder="example@gmail.com"
          onChange={handleChangeCorreo}
        />
         <p style={{ color: 'red' }}>{correoError}</p>
      </div>

      


 
      
      <button type="submit">Guardar</button>
    </form>
    
    
      </div>


  );


}
export default FormularioRegistroParticipantes;
