import React, { useState } from 'react';
import axios from 'axios';
import './assets/configurarEvento.css';

const ConfiguracionEvento = () => {
  const opciones = ['Descripción', 'Requisitos', 'Contenido', 'Reglas', 'Premios', 'E-mail', 'Telefono', 'Participante', ];
  const [opcionesSeleccionadas, setOpcionesSeleccionadas] = useState([]);
  const [participanteSeleccionado, setParticipanteSeleccionado] = useState(null);

  const handleOpcionClick = (opcion) => {
    if (opcion === 'Individual' || opcion === 'Grupal') {
      setParticipanteSeleccionado(opcion);
    } else {
      // Implementa lógica para agregar o quitar opciones seleccionadas
      if (opcionesSeleccionadas.includes(opcion)) {
        setOpcionesSeleccionadas(opcionesSeleccionadas.filter((item) => item !== opcion));
      } else {
        setOpcionesSeleccionadas([...opcionesSeleccionadas, opcion]);
      }
    }
  };

  const renderOpcionesColumna = (opcionesArray) => {
    return opcionesArray.map((opcion) => (
      <label key={opcion}>
        <input
          type="checkbox"
          checked={opcionesSeleccionadas.includes(opcion)}
          onChange={() => handleOpcionClick(opcion)}
        /> {opcion}
      </label>
    ));
  };

  return (
    <div className="formulario-container">
      <h2>Campos requeridos para el evento</h2>
      <div className="columnas-container">
        <div className="columna">
          {renderOpcionesColumna(opciones.slice(0, 4))}
        </div>
        <div className="columna">
          {renderOpcionesColumna(opciones.slice(4, 8))}
        </div>
      </div>
      <div className="botones-container">
        <button className="boton-cancelar">Cancelar</button>
        <button className="boton-siguiente">Siguiente</button>
      </div>
      {participanteSeleccionado && (
        <p>Opción de participante seleccionada: {participanteSeleccionado}</p>
      )}
    </div>
  );
};

export default ConfiguracionEvento;
