import React, { useState } from 'react';
import './assets/ModalSeleccionRegistro.css';
import CrearEquipo from './FormularioRegistrarEquipo';
import RegistrarseEquipo from './FormularioRegistrarParticipantesToEquipo';
import { useNavigate } from 'react-router-dom';
const seleccionRegistro = ({ EventoId, onClose }) => {


    const [mostrarComponente1, setMostrarComponente1] = useState(true);
    const [mostrarComponente2, setMostrarComponente2] = useState(false);
  
    const mostrarPrimerComponente = () => {
      setMostrarComponente1(true);
      setMostrarComponente2(false);
      onClose();
    };
  
    const mostrarSegundoComponente = () => {
      setMostrarComponente1(false);
      setMostrarComponente2(true);
      onClose();
    };
 
 
  return (
    <div className='modal-container'>
      <div className='modal-content error'>
      <span className="cerrar" onClick={onClose}>&times;</span>
       <h1 >Selecciona la accion que deseas</h1>
        <div className='boton-ok'>
          
        <button onClick={mostrarPrimerComponente}>Registrar el nombre del equipo</button>
      </div>
      <div className='boton-ok'>
          <button onClick={mostrarSegundoComponente}>Registrarse a un equipo</button>
        
        </div>
        {mostrarComponente1 && <CrearEquipo evento={EventoId} />}
        {mostrarComponente2 && <RegistrarseEquipo evento={EventoId} />}
      </div>
    </div>
  );
};
export default seleccionRegistro;
