import React, { useEffect,useState } from 'react';
import './assets/ModalRegistroParticipantes.css';
import axios from './api/conexionApi';
import SuccessMessage from './ModalRegistroExitoso';
import ErrorMessage from './ModalErrorRegistro';
import ModalSalir from './ModalSalirRegistroEntrenador';
import validate from './utils/Validaciones';





const FormularioRegistroEntrenador= ({ evento,idEquipoE, onClose }) => {
  const [mostrarModalSalir, setMostrarModalSalir] = useState(false);
  const handleInicioClick = () => {
    setMostrarModalSalir(true);
  };
  const handleCloseModalSalir = () => {
    setMostrarModalSalir(false);
  };

 //mensaje modal error
  const [showErrorModal, setShowErrorModal] = useState(false);

  const closeErrorModal = () => {
    setShowErrorModal(false);
  };
  //modal error

//mensajeModal exito
const [registroExitoso, setRegistroExitoso] = useState(false);
//fin modal exito

  const [formData, setFormData] = useState({
    idEntrenador:'',
    nombreEntrenador:'',
    apellidoEntrenador:'',
    correoEntrenador:'',
    idEquipo:idEquipoE
  });
 
 //para el error

  const [mensajeError, setMensajeError] = useState({
    ciEntrenadorError:'',
    nombreEntrenadorError:'',
    apellidoEntrenadorError:'',
    correoEntrenadorError:'',   
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
      switch (name) {
      case 'nombreEntrenador':
        const nombreRes=validate.validarNombre(value);
        setMensajeError((mensajeError) => ({ ...mensajeError, nombreParticipanteError: nombreRes }));
        if(nombreRes===""){
          setFormData({
            ...formData,
            [name]: value,
        })
      } else{
        const nombreNew=validate.devolverNombre(value);
        setFormData({
          ...formData,
          [name]: nombreNew,
      })
      }      
        break;
          case 'apellidoEntrenador':
            const apellidoRes=validate.validarNombre(value);
            setMensajeError((mensajeError) => ({ ...mensajeError, ApellidoParticipanteError: apellidoRes }));
            if(apellidoRes===""){
              const apellidoNew=validate.devolverNombre(value);
              setFormData({
                ...formData,
                [name]: apellidoNew,
            })
          }

            break;
            case 'idEntrenador':
              const ciRes=validate.validarCI(value);
              setMensajeError((mensajeError) => ({ ...mensajeError, ciParticipanteError: ciRes }));
              if(ciRes===""){
                setFormData({
                  ...formData,
                  [name]: value,
              })
                }else{
                const ciNew=validate.devolverCI(value);
                    setFormData({
                    ...formData,
                    [name]: ciNew,})
                }
                break;
      

            case 'correoEntrenador':
                const telefonoRes=validate.validarTelefono(value);
                setMensajeError((mensajeError) => ({ ...mensajeError, telefonoParticipanteError: telefonoRes }));
                if(telefonoRes===""){
                setFormData({
                    ...formData,
                    [name]: value,
                })
            }else{
                const telefonoNew=validate.devolverTelefono(value);
                setFormData({
                    ...formData,
                    [name]: telefonoNew,
                })
            }
                break;

            default:
            setFormData({
            ...formData,
            [name]: value,
            });
    }
};
 
  const handleSubmit = (e) => {
    //ERRORES
   
    setMensajeError((mensajeError) => ({ ...mensajeError, nombreEntrenadorError: validate.validarCampoVacio(formData.nombreEntrenador) }));
  setMensajeError((mensajeError) => ({ ...mensajeError, apellidoEntrenadorError: validate.validarCampoVacio(formData.apellidoEntrenador) }));
  setMensajeError((mensajeError) => ({ ...mensajeError, ciEntrenadorError: validate.validarCampoVacio(formData.idEntrenador) }));
  setMensajeError((mensajeError) => ({ ...mensajeError, correoParticipanteError: validate.validarCampoVacio(formData.correoEntrenador) }));

//ERRORES
    e.preventDefault();

    axios.post(`./storeEntrenador/${evento}`, formData)
      .then((a) => {
      setRegistroExitoso(true);
    })
    .catch((error) => {       
      console.error('Error al guardar los datos de persona', error);
      setShowErrorModal(true);
      });
      
  }



  return (
    <div className='modal-container-MRPE'>
      <div className='modal-content-MRPE MRPE'>
        
      <div className='contenedor-formulario-ToE-MRPE'>
    <br/>
      <h1 className='centrar-titulo'>Formulario de Registro de Participante</h1>
      <br/>
    <form onSubmit={handleSubmit}>
          
      <div>
        <label class="subtitulo required"  htmlFor="nombreEntrenador">Nombre:</label>
        <input
          type="text"
          id="nombreEntrenador"
          name="nombreEntrenador"
          value={formData.nombreEntrenador}
          placeholder="Ingresa tu nombre"
          onChange={handleChange}
        />
         <p style={{ color: 'red' }}>{mensajeError.nombreEntrenadorError}</p>
      </div>
      <br/>
      <div>
        <label class="subtitulo required" htmlFor="apellidoEntrenador">Apellido:</label>
        <input
          type="text"
          id="apellidoEntrenador"
          name="apellidoEntrenador"
          value={formData.apellidoEntrenador}
          placeholder="Ingresa tus apellidos"
          onChange={handleChange}          
        />
         <p style={{ color: 'red' }}>{mensajeError.apellidoEntrenadorError}</p>
      </div>
      <br/>
      <div>
        <label class="subtitulo required" htmlFor="idEntrenador">CI: </label>
        <input
          type="text"
          id="idEntrenador"
          name="idEntrenador"
          value={formData.idEntrenador}
          placeholder="Ingresa tu numero de identificacion"
          onChange={handleChange}          
        />
         <p style={{ color: 'red' }}>{mensajeError.ciEntrenadorError}</p>
      </div>
      <br/>
  
      <div>
        <label class="subtitulo required" htmlFor="correoEntrenador">Correo:</label>
        <input
          type="text"
          id="correoEntrenador"
          name="correoEntrenador"
          value={formData.correoEntrenador}
          placeholder="example@gmail.com"
          onChange={handleChange}
        />
         <p style={{ color: 'red' }}>{mensajeError.correoEntrenadorError}</p>
      </div>
      <br/>
       <div className='button-container-ToE-MRPE '>
          <button className=".blue-button-ToE-MRPE" type="button" onClick={() => handleInicioClick()}>Salir</button>
          <button className=".blue-button-ToE-MRPE" type="submit">Registrar</button>
       </div>
       <br/>
       <br/>

  </form>

      {registroExitoso && <SuccessMessage message="¡Registro exitoso!" />}
      {showErrorModal && (
        <ErrorMessage message="Ha ocurrido un error al realizar el registro, intentelo nuevamente" onClose={closeErrorModal} />
      )}
      {mostrarModalSalir && (
        <ModalSalir message="¿Quiere abandonar el registro?" onClose={handleCloseModalSalir} />
      )}
    </div>
      </div>
    </div>
  );
};
export default FormularioRegistroEntrenador;
